pragma solidity ^0.8.9;

import "@openzeppelin/contracts/utils/cryptography/ECDSA.sol";
import "@openzeppelin/contracts/utils/cryptography/EIP712.sol";

/**
 * @dev Simple minimal forwarder to be used together with an ERC2771 compatible contract. See {ERC2771Context}.
 *
 * MinimalForwarder is mainly meant for testing, as it is missing features to be a good production-ready forwarder. This
 * contract does not intend to have all the properties that are needed for a sound forwarding system. A fully
 * functioning forwarding system with good properties requires more complexity. We suggest you look at other projects
 * such as the GSN which do have the goal of building a system like that.
 *
 * While there are a few features missing for a general forwarder (like advanced domain and request management),
 * there is almost no difference to the GSN v3 forwarder when it comes to request validation.
 */
contract StaxeForwarder is EIP712 {
  using ECDSA for bytes32;

  struct ForwardRequest {
    address from;
    address to;
    uint256 value;
    uint256 gas;
    uint256 nonce;
    bytes data;
  }

  bytes32 private constant _TYPEHASH =
    keccak256("ForwardRequest(address from,address to,uint256 value,uint256 gas,uint256 nonce,bytes data)");

  mapping(address => uint256) private _nonces;

  constructor() EIP712("Staxe", "1.0.0") {}

  function getNonce(address from) public view returns (uint256) {
    return _nonces[from];
  }

  function verify(ForwardRequest calldata req, bytes calldata signature) public view returns (bool) {
    address signer = _hashTypedDataV4(
      keccak256(abi.encode(_TYPEHASH, req.from, req.to, req.value, req.gas, req.nonce, keccak256(req.data)))
    ).recover(signature);
    return _nonces[req.from] == req.nonce && signer == req.from;
  }

  function execute(ForwardRequest calldata req, bytes calldata signature) public payable returns (bool, bytes memory) {
    require(verify(req, signature), "StaxeForwarder: signature does not match request");
    _nonces[req.from] = req.nonce + 1;

    (bool success, bytes memory returndata) = req.to.call{gas: req.gas, value: req.value}(
      abi.encodePacked(req.data, req.from)
    );

    // Validate that the relayer has sent enough gas for the call.
    // See https://ronan.eth.limo/blog/ethereum-gas-dangers/
    if (gasleft() <= req.gas / 63) {
      // We explicitly trigger invalid opcode to consume all gas and bubble-up the effects, since
      // neither revert or assert consume all gas since Solidity 0.8.0
      // https://docs.soliditylang.org/en/v0.8.0/control-structures.html#panic-via-assert-and-error-via-require
      /// @solidity memory-safe-assembly
      assembly {
        invalid()
      }
    }

    return (success, returndata);
  }
}

contracts/v3/StaxeMembersV3.sol.content:
//SPDX-License-Identifier: MIT

pragma solidity ^0.8.9;

import "@openzeppelin/contracts-upgradeable/access/AccessControlUpgradeable.sol";
import "@openzeppelin/contracts-upgradeable/access/AccessControlEnumerableUpgradeable.sol";
import "@openzeppelin/contracts-upgradeable/metatx/ERC2771ContextUpgradeable.sol";

import "./interfaces/IMembersV3.sol";

// import "hardhat/console.sol";

/// @custom:security-contact info@staxe.io
contract StaxeMembersV3 is AccessControlEnumerableUpgradeable, IMembersV3 {
  bytes32 public constant INVESTOR_ROLE = keccak256("INVESTOR_ROLE");
  bytes32 public constant ORGANIZER_ROLE = keccak256("ORGANIZER_ROLE");
  bytes32 public constant APPROVER_ROLE = keccak256("APPROVER_ROLE");

  mapping(address => address) organizerDelegate;
  address private relayer;

  // ---- Events ----

  // ---- Functions ----

  /// @custom:oz-upgrades-unsafe-allow constructor
  constructor() {
    _disableInitializers();
  }

  function initialize(address treasury, address _relayer) public initializer {
    __AccessControlEnumerable_init();
    relayer = _relayer;
    _setupRole(AccessControlUpgradeable.DEFAULT_ADMIN_ROLE, msg.sender);
    _setupRole(INVESTOR_ROLE, _msgSender());
    _setupRole(ORGANIZER_ROLE, _msgSender());
    _setupRole(APPROVER_ROLE, _msgSender());
    _grantRole(AccessControlUpgradeable.DEFAULT_ADMIN_ROLE, relayer);
    _grantRole(AccessControlUpgradeable.DEFAULT_ADMIN_ROLE, treasury);
  }

  function isOrganizer(address sender) external view override returns (bool) {
    return hasRole(ORGANIZER_ROLE, sender);
  }

  function isApprover(address sender) external view override returns (bool) {
    return hasRole(APPROVER_ROLE, sender);
  }

  function isInvestor(address sender) external view override returns (bool) {
    return hasRole(INVESTOR_ROLE, sender);
  }

  function isOrganizerDelegate(address sender, address organizer) external view override returns (bool) {
    return hasRole(ORGANIZER_ROLE, organizer) && (sender == organizer || organizerDelegate[sender] == organizer);
  }

  function addDelegate(address delegate) external onlyRole(ORGANIZER_ROLE) {
    require(organizerDelegate[delegate] == address(0), "Delegate already added");
    organizerDelegate[delegate] = _msgSender();
  }

  function removeDelegate(address delegate) external onlyRole(ORGANIZER_ROLE) {
    require(organizerDelegate[delegate] == _msgSender(), "Cannot remove delegate from someone else");
    organizerDelegate[delegate] = address(0);
  }

  function registerInvestor(address investor) external {
    require(relayer == msg.sender, "Can only be called from trusted relayer");
    _grantRole(INVESTOR_ROLE, investor);
  }
}

contracts/v3/StaxeProductionEscrowV3.sol.content:
//SPDX-License-Identifier: MIT

pragma solidity ^0.8.9;

import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts-upgradeable/token/ERC1155/IERC1155Upgradeable.sol";
import "@openzeppelin/contracts/token/ERC1155/IERC1155Receiver.sol";
import "@openzeppelin/contracts/utils/structs/EnumerableSet.sol";
import "@openzeppelin/contracts-upgradeable/token/ERC20/utils/SafeERC20Upgradeable.sol";
import "@openzeppelin/contracts/security/Pausable.sol";

import "./interfaces/IMembersV3.sol";
import "./interfaces/IProductionEscrowV3.sol";

// import "hardhat/console.sol";

contract StaxeProductionEscrowV3 is Ownable, IProductionEscrowV3, IERC1155Receiver, Pausable {
  using EnumerableSet for EnumerableSet.UintSet;
  using SafeERC20Upgradeable for IERC20Upgradeable;

  // --- Data ---

  ProductionData public productionData;
  uint256 public immutable tokenPrice;
  IMembersV3 public immutable members;

  Perk[] public perks;
  mapping(address => uint16[]) public perksByOwner;
  mapping(address => EnumerableSet.UintSet) private perkSetByOwner;

  IERC1155Upgradeable private tokenContract;

  uint256 public fundsRaised;
  uint256 public proceedsEarned;
  mapping(address => uint256) payoutPerTokenHolder;
  mapping(address => uint256) private payoutPerTokenTracking;
  mapping(address => Purchase[]) private purchasesByBuyer;

  bool public refundable;

  // --- Functions ---

  constructor(
    ProductionData memory _productionData,
    Perk[] memory _perks,
    uint256 _tokenPrice,
    IMembersV3 _members
  ) Ownable() {
    productionData = _productionData;
    for (uint16 i = 0; i < _perks.length; i++) {
      perks.push(_perks[i]);
    }
    tokenPrice = _tokenPrice;
    members = _members;
  }

  modifier hasState(ProductionState state) {
    require(productionData.state == state, "Not in required state");
    _;
  }

  modifier creatorOnly(address caller) {
    require(
      isCreatorOrDelegate(caller) || (refundable && members.isApprover(caller)),
      "Can only be called from creator or delegate"
    );
    _;
  }

  // --- IProductionEscrowV3 functions ---

  function getProductionData() external view override returns (ProductionData memory) {
    return productionData;
  }

  function getProductionDataWithPerks()
    external
    view
    override
    returns (
      ProductionData memory,
      Perk[] memory,
      uint256,
      uint256
    )
  {
    return (productionData, perks, fundsRaised, proceedsEarned);
  }

  function getTokensAvailable() external view hasState(ProductionState.OPEN) returns (uint256) {
    return productionData.totalSupply - productionData.soldCounter;
  }

  function getTokenPrice(uint256 amount, address buyer) external view override returns (IERC20Upgradeable, uint256) {
    return productionData.priceCalculationEngine.calculateTokenPrice(this, productionData, tokenPrice, amount, buyer);
  }

  function getTokenOwnerData(address tokenOwner)
    external
    view
    override
    returns (
      uint256 balance,
      Purchase[] memory purchases,
      Perk[] memory perksOwned,
      uint256 proceedsClaimed,
      uint256 proceedsAvailable
    )
  {
    balance = tokenContract.balanceOf(tokenOwner, productionData.id);
    uint16[] memory ids = perksByOwner[tokenOwner];
    uint256[] memory values = EnumerableSet.values(perkSetByOwner[tokenOwner]);
    perksOwned = new Perk[](values.length);
    for (uint16 i = 0; i < values.length; i++) {
      uint16 id = uint16(values[i]);
      Perk memory perk = perks[id - 1];
      uint16 count = countIds(id, ids);
      perksOwned[i] = Perk({id: id, total: perk.total, claimed: count, minTokensRequired: perk.minTokensRequired});
    }
    proceedsClaimed = payoutPerTokenHolder[tokenOwner];
    proceedsAvailable = productionData.soldCounter > 0
      ? ((balance * proceedsEarned) / productionData.soldCounter) - payoutPerTokenTracking[tokenOwner]
      : 0;
    return (balance, purchasesByBuyer[tokenOwner], perksOwned, proceedsClaimed, proceedsAvailable);
  }

  // ---------------------------------------------------------------------------------------------
  // Lifecycle
  // ---------------------------------------------------------------------------------------------

  function approve(address approver) external override hasState(ProductionState.CREATED) onlyOwner {
    require(members.isApprover(approver));
    emit StateChanged(ProductionState.CREATED, ProductionState.OPEN, approver);
    productionData.state = ProductionState.OPEN;
  }

  function decline(address decliner) external override hasState(ProductionState.CREATED) onlyOwner {
    require(members.isApprover(decliner));
    emit StateChanged(ProductionState.CREATED, ProductionState.DECLINED, decliner);
    productionData.state = ProductionState.DECLINED;
  }

  function finish(
    address caller,
    bool isTrustedForwarder,
    address platformTreasury
  ) external override hasState(ProductionState.OPEN) onlyOwner whenNotPaused {
    // if we have an end timestamp we can allow closing anyone (e.g. our relay with an autotask)
    // otherwise if no timestamp we hand this over to the production owner.
    require(
      (productionData.crowdsaleEndDate == 0 && isCreatorOrDelegate(caller)) ||
        (productionData.crowdsaleEndDate > 0 &&
          productionData.crowdsaleEndDate <= block.timestamp &&
          (isCreatorOrDelegate(caller) || isTrustedForwarder)),
      "Cannot be finished before finish date or only by creator"
    );
    emit StateChanged(ProductionState.OPEN, ProductionState.FINISHED, caller);
    productionData.state = ProductionState.FINISHED;
    swipeToCreator(caller, platformTreasury);
  }

  function close(
    address caller,
    bool isTrustedForwarder,
    address platformTreasury
  ) external override hasState(ProductionState.FINISHED) onlyOwner {
    // if we have an end timestamp we can allow closing anyone (e.g. our relay with an autotask)
    // otherwise if no timestamp we hand this over to the production owner.
    require(
      (productionData.productionEndDate == 0 && isCreatorOrDelegate(caller)) ||
        (productionData.productionEndDate > 0 &&
          productionData.productionEndDate <= block.timestamp &&
          (isCreatorOrDelegate(caller) || isTrustedForwarder)),
      "Cannot be closed before close date or only by creator"
    );
    emit StateChanged(ProductionState.FINISHED, ProductionState.CLOSED, caller);
    productionData.state = ProductionState.CLOSED;
    swipeToCreator(caller, platformTreasury);
  }

  function pause(address caller) external onlyOwner hasState(ProductionState.OPEN) whenNotPaused {
    require(members.isApprover(caller), "Caller must be approver");
    _pause();
  }

  function unpause(address caller) external onlyOwner hasState(ProductionState.OPEN) whenPaused {
    require(members.isApprover(caller), "Caller must be approver");
    _unpause();
  }

  function paused() public view override(Pausable, IProductionEscrowV3) onlyOwner returns (bool) {
    return Pausable.paused();
  }

  function cancel(address caller, uint256 newCloseDate) external onlyOwner hasState(ProductionState.OPEN) whenPaused {
    require(members.isApprover(caller), "Caller must be approver");
    require(newCloseDate >= block.timestamp + 30 days, "Refund period not long enough");
    emit StateChanged(ProductionState.OPEN, ProductionState.FINISHED, caller);
    productionData.state = ProductionState.FINISHED;
    productionData.productionEndDate = newCloseDate;
    refundable = true;
    IERC20Upgradeable currency = IERC20Upgradeable(productionData.currency);
    uint256 balance = currency.balanceOf(address(this));
    proceedsEarned += balance;
    _unpause();
  }

  // ---------------------------------------------------------------------------------------------
  // Tokens, funds and proceeds
  // ---------------------------------------------------------------------------------------------

  function buyTokens(
    address buyer,
    uint256 amount,
    uint256 price,
    uint16 perkId
  ) external override hasState(ProductionState.OPEN) whenNotPaused onlyOwner {
    require(amount <= productionData.totalSupply - productionData.soldCounter, "Not enough tokens available");
    require(
      members.isInvestor(buyer) ||
        amount <= productionData.maxTokensUnknownBuyer ||
        productionData.maxTokensUnknownBuyer == 0,
      "Needs investor role to buy amount of tokens"
    );
    claimPerk(buyer, amount, perkId);
    fundsRaised += price;
    emit TokenBought(buyer, amount, price, perkId);
    productionData.soldCounter += amount;
    tokenContract.safeTransferFrom(address(this), buyer, productionData.id, amount, "");
    purchasesByBuyer[buyer].push(Purchase(amount, price));
  }

  function depositProceeds(address caller, uint256 amount)
    external
    override
    hasState(ProductionState.FINISHED)
    creatorOnly(caller)
    onlyOwner
  {
    emit ProceedsDeposited(amount, caller);
    proceedsEarned += amount;
  }

  function transferProceeds(address holder)
    external
    override
    hasState(ProductionState.FINISHED)
    onlyOwner
    returns (uint256 payout)
  {
    require(members.isInvestor(holder), "Only investors can claim proceeds");
    uint256 tokens = tokenContract.balanceOf(holder, productionData.id);
    payout = ((tokens * proceedsEarned) / productionData.soldCounter) - payoutPerTokenTracking[holder];
    payoutPerTokenHolder[holder] += payout;
    payoutPerTokenTracking[holder] += payout;
    if (payout > 0) {
      emit ProceedsClaimed(payout, holder);
      IERC20Upgradeable token = IERC20Upgradeable(productionData.currency);
      token.safeTransfer(holder, payout);
    }
  }

  function transferFunding(address caller, address platformTreasury)
    external
    override
    hasState(ProductionState.OPEN)
    creatorOnly(caller)
    onlyOwner
    whenNotPaused
    returns (uint256 amount, uint256 platformShare)
  {
    (amount, platformShare) = swipeToCreator(caller, platformTreasury);
  }

  // ---------------------------------------------------------------------------------------------
  // Callbacks
  // ---------------------------------------------------------------------------------------------

  function onTokenTransfer(
    IERC1155Upgradeable, /* tokenContract */
    uint256 tokenId,
    address currentOwner,
    address newOwner,
    uint256 numTokens
  ) external override {
    require(msg.sender == address(tokenContract), "Unknown token contract sender");
    require(tokenId == productionData.id, "Invalid token id");
    require(numTokens > 0, "Tokens to transfer must be > 0");
    uint256 currentBalance = tokenContract.balanceOf(currentOwner, productionData.id);
    require(currentBalance >= numTokens, "Insufficient balance for transfer");
    uint256 payoutTransfer = (numTokens * payoutPerTokenTracking[currentOwner]) / currentBalance;
    payoutPerTokenTracking[currentOwner] -= payoutTransfer;
    payoutPerTokenTracking[newOwner] += payoutTransfer;
  }

  function onERC1155Received(
    address, /*operator*/
    address, /*from*/
    uint256 tokenId,
    uint256 amount,
    bytes calldata /*data*/
  ) external virtual override hasState(ProductionState.CREATED) returns (bytes4) {
    require(productionData.id == 0, "Token already set");
    require(productionData.totalSupply == amount, "Wrong amount minted");
    productionData.id = tokenId;
    tokenContract = IERC1155Upgradeable(msg.sender);
    if (productionData.organizerTokens > 0) {
      tokenContract.safeTransferFrom(
        address(this),
        productionData.creator,
        tokenId,
        productionData.organizerTokens,
        bytes("")
      );
      productionData.soldCounter += productionData.organizerTokens;
    }
    return this.onERC1155Received.selector;
  }

  function onERC1155BatchReceived(
    address, /*operator*/
    address, /*from*/
    uint256[] calldata, /*tokenIds*/
    uint256[] calldata, /*values*/
    bytes calldata /*data*/
  ) external virtual override returns (bytes4) {
    return 0x00; // unsupported
  }

  // ERC165 - needed from ERC1155Receiver
  function supportsInterface(bytes4 interfaceID) external view virtual override returns (bool) {
    return
      interfaceID == 0x01ffc9a7 || // ERC-165 support (i.e. `bytes4(keccak256('supportsInterface(bytes4)'))`).
      interfaceID == 0x4e2312e0; // ERC-1155 `ERC1155TokenReceiver` support (i.e. `bytes4(keccak256("onERC1155Received(address,address,uint256,uint256,bytes)")) ^ bytes4(keccak256("onERC1155BatchReceived(address,address,uint256[],uint256[],bytes)"))`).
  }

  // ---------------------------------------------------------------------------------------------
  // private
  // ---------------------------------------------------------------------------------------------

  function swipeToCreator(address caller, address platformTreasury)
    private
    returns (uint256 balanceLeft, uint256 platformShare)
  {
    IERC20Upgradeable currency = IERC20Upgradeable(productionData.currency);
    uint256 balance = currency.balanceOf(address(this));
    platformShare = (balance * productionData.platformSharePercentage) / 100;
    balanceLeft = balance - platformShare;
    emit FundingClaimed(balanceLeft, platformShare, caller);
    currency.safeTransfer(productionData.creator, balanceLeft);
    currency.safeTransfer(platformTreasury, platformShare);
  }

  function isCreatorOrDelegate(address caller) private view returns (bool) {
    return productionData.creator == caller || members.isOrganizerDelegate(caller, productionData.creator);
  }

  function claimPerk(
    address buyer,
    uint256 tokensBought,
    uint16 perkId
  ) internal {
    if (perkId == 0) {
      return;
    }
    require(perkId <= perks.length, "Invalid perkId");
    Perk storage perk = perks[perkId - 1];
    require(perk.total > perk.claimed, "Perk not available");
    require(perk.minTokensRequired <= tokensBought, "Not enough tokens to claim");
    perk.claimed += 1;
    perksByOwner[buyer].push(perkId);
    EnumerableSet.add(perkSetByOwner[buyer], perkId);
    if (address(productionData.perkTracker) != address(0)) {
      productionData.perkTracker.perkClaimed(buyer, productionData.id, perkId, tokensBought);
    }
  }

  function countIds(uint16 perkId, uint16[] memory perkIds) private pure returns (uint16 result) {
    for (uint16 i = 0; i < perkIds.length; i++) {
      if (perkIds[i] == perkId) {
        result += 1;
      }
    }
  }
}

contracts/v3/StaxeProductionsFactoryV3.s.content:
//SPDX-License-Identifier: MIT

pragma solidity ^0.8.9;

import "@openzeppelin/contracts-upgradeable/token/ERC20/IERC20Upgradeable.sol";

import "./interfaces/IMembersV3.sol";
import "./interfaces/IPerkTrackerV3.sol";
import "./interfaces/IPriceCalculationEngineV3.sol";
import "./interfaces/IProductionsV3.sol";
import "./interfaces/IProductionEscrowV3.sol";
import "./StaxeProductionEscrowV3.sol";

// import "hardhat/console.sol";

contract StaxeProductionsFactoryV3 {
  // ----- Structs -----
  struct CreatePerk {
    uint16 total;
    uint256 minTokensRequired;
  }

  struct CreateProduction {
    uint256 totalSupply;
    uint256 organizerTokens;
    uint256 tokenPrice;
    address currency;
    uint256 maxTokensUnknownBuyer;
    CreatePerk[] perks;
    string dataHash;
    uint256 crowdsaleEndDate;
    uint256 productionEndDate;
    uint8 platformSharePercentage;
    address perkTracker;
  }

  // ----- Events -----
  event ProductionCreated(uint256 indexed id, address indexed creator, uint256 tokenSupply, address escrow);

  // ----- State -----
  IProductionsV3 private productions;
  IMembersV3 private members;
  IPriceCalculationEngineV3 private priceCalculationEngine;

  constructor(
    IProductionsV3 _productions,
    IMembersV3 _members,
    IPriceCalculationEngineV3 _priceCalculationEngine
  ) {
    productions = _productions;
    members = _members;
    priceCalculationEngine = _priceCalculationEngine;
  }

  // ----- Functions -----
  function createProduction(CreateProduction calldata data) external {
    require(members.isOrganizer(msg.sender), "Not an organizer");
    IProductionEscrowV3.Perk[] memory perks = new IProductionEscrowV3.Perk[](data.perks.length);
    for (uint16 i = 0; i < perks.length; i++) {
      CreatePerk memory perk = data.perks[i];
      perks[i] = IProductionEscrowV3.Perk({
        id: i + 1,
        total: perk.total,
        claimed: 0,
        minTokensRequired: perk.minTokensRequired
      });
    }
    IProductionEscrowV3.ProductionData memory productionData = IProductionEscrowV3.ProductionData({
      id: 0,
      creator: msg.sender,
      totalSupply: data.totalSupply,
      organizerTokens: data.organizerTokens,
      maxTokensUnknownBuyer: data.maxTokensUnknownBuyer,
      soldCounter: 0,
      currency: IERC20Upgradeable(data.currency),
      state: IProductionEscrowV3.ProductionState.CREATED,
      dataHash: data.dataHash,
      crowdsaleEndDate: data.crowdsaleEndDate,
      productionEndDate: data.productionEndDate,
      platformSharePercentage: data.platformSharePercentage,
      perkTracker: IPerkTrackerV3(data.perkTracker),
      priceCalculationEngine: IPriceCalculationEngineV3(priceCalculationEngine)
    });
    StaxeProductionEscrowV3 escrow = new StaxeProductionEscrowV3(productionData, perks, data.tokenPrice, members);
    escrow.transferOwnership(address(productions));
    if (data.perkTracker != address(0)) {
      productionData.perkTracker.registerEscrow(escrow);
    }
    uint256 id = productions.mintProduction(escrow, msg.sender, data.totalSupply);
    emit ProductionCreated(id, msg.sender, data.totalSupply, address(escrow));
  }
}

contracts/v3/StaxeProductionsV3.sol.content:
//SPDX-License-Identifier: MIT

pragma solidity ^0.8.9;

import "@openzeppelin/contracts-upgradeable/proxy/utils/Initializable.sol";
import "@openzeppelin/contracts-upgradeable/metatx/ERC2771ContextUpgradeable.sol";
import "@openzeppelin/contracts-upgradeable/access/OwnableUpgradeable.sol";
import "@openzeppelin/contracts-upgradeable/utils/CountersUpgradeable.sol";
import "@openzeppelin/contracts-upgradeable/utils/AddressUpgradeable.sol";
import "@openzeppelin/contracts-upgradeable/security/ReentrancyGuardUpgradeable.sol";
import "@openzeppelin/contracts-upgradeable/token/ERC20/IERC20Upgradeable.sol";
import "@openzeppelin/contracts-upgradeable/token/ERC20/utils/SafeERC20Upgradeable.sol";

import "@uniswap/v3-periphery/contracts/interfaces/ISwapRouter.sol";
import "@uniswap/v3-periphery/contracts/libraries/TransferHelper.sol";

import "./interfaces/IProductionEscrowV3.sol";
import "./interfaces/IProductionTokenV3.sol";
import "./interfaces/IProductionTokenTrackerV3.sol";
import "./interfaces/IProductionsV3.sol";
import "./interfaces/IMembersV3.sol";
import "./interfaces/IWETH.sol";

//import "hardhat/console.sol";

/// @custom:security-contact info@staxe.io
contract StaxeProductionsV3 is
  ERC2771ContextUpgradeable,
  OwnableUpgradeable,
  ReentrancyGuardUpgradeable,
  IProductionsV3
{
  using CountersUpgradeable for CountersUpgradeable.Counter;
  using AddressUpgradeable for address payable;
  using SafeERC20Upgradeable for IERC20Upgradeable;

  // ---------- State ----------

  CountersUpgradeable.Counter private tokenIds;
  mapping(address => bool) public trustedEscrowFactories;
  mapping(address => bool) public trustedErc20Coins;

  ISwapRouter public constant router = ISwapRouter(0xE592427A0AEce92De3Edee1F18E0157C05861564);

  mapping(uint256 => Escrow) public productionEscrows;
  mapping(address => uint256[]) public productionIdsByOwner;
  IProductionTokenV3 public productionToken;
  IMembersV3 public members;
  address public treasury;
  IWETH public nativeWrapper;
  address private relayer;

  // ---------- Functions ----------

  /// @custom:oz-upgrades-unsafe-allow constructor
  constructor(address trustedForwarder) ERC2771ContextUpgradeable(trustedForwarder) {
    _disableInitializers();
  }

  // ---- Modifiers ----

  modifier validProduction(uint256 id) {
    require(productionEscrows[id].id != 0, "Production does not exist");
    _;
  }

  modifier validBuyer(address buyer) {
    require(buyer != address(0), "Buyer must be valid address");
    // 1. Anyone can buy for own address
    // 2. Investors can buy for other addresses
    require(buyer == _msgSender() || members.isInvestor(_msgSender()), "Invalid token buyer");
    _;
  }

  modifier relayerOnly() {
    require(msg.sender == relayer, "Can only be called from trusted relayer");
    _;
  }

  // ---- Proxy ----

  function initialize(
    IProductionTokenV3 _productionToken,
    IMembersV3 _members,
    IWETH _nativeWrapper,
    address _treasury,
    address _relayer
  ) public initializer {
    __Ownable_init();
    __ReentrancyGuard_init();

    require(_treasury != address(0), "Treasury must be valid address");

    productionToken = _productionToken;
    members = _members;
    nativeWrapper = _nativeWrapper;
    treasury = _treasury;
    relayer = _relayer;
    tokenIds.increment();
  }

  // ---- Data Access ----

  function getProduction(uint256 id) external view override returns (Production memory) {
    if (productionEscrows[id].id == 0) {
      return emptyProduction(id);
    }
    (
      IProductionEscrowV3.ProductionData memory data,
      IProductionEscrowV3.Perk[] memory perks,
      uint256 fundsRaised,
      uint256 proceedsEarned
    ) = productionEscrows[id].escrow.getProductionDataWithPerks();
    uint256 balance = IERC20Upgradeable(data.currency).balanceOf(address(productionEscrows[id].escrow));
    bool paused = productionEscrows[id].escrow.paused();
    return
      Production({
        id: id,
        data: data,
        perks: perks,
        escrow: productionEscrows[id].escrow,
        fundsRaised: fundsRaised,
        proceedsEarned: proceedsEarned,
        escrowBalance: balance,
        paused: paused
      });
  }

  function getTokenPrice(uint256 id, uint256 amount)
    external
    view
    override
    validProduction(id)
    returns (IERC20Upgradeable, uint256)
  {
    return productionEscrows[id].escrow.getTokenPrice(amount, _msgSender());
  }

  function getTokenPriceFor(
    uint256 id,
    uint256 amount,
    address buyer
  ) external view override validProduction(id) validBuyer(buyer) returns (IERC20Upgradeable, uint256) {
    return productionEscrows[id].escrow.getTokenPrice(amount, buyer);
  }

  function getTokenOwnerData(uint256 id, address tokenOwner)
    external
    view
    override
    validProduction(id)
    validBuyer(tokenOwner)
    returns (
      uint256 balance,
      IProductionEscrowV3.Purchase[] memory purchases,
      IProductionEscrowV3.Perk[] memory perksOwned,
      uint256 proceedsClaimed,
      uint256 proceedsAvailable
    )
  {
    return productionEscrows[id].escrow.getTokenOwnerData(tokenOwner);
  }

  function getProductionIdsByCreator(address creator) external view override returns (uint256[] memory) {
    return productionIdsByOwner[creator];
  }

  // ---- Lifecycle ----

  function mintProduction(
    IProductionEscrowV3 escrow,
    address creator,
    uint256 totalAmount
  ) external override nonReentrant returns (uint256 id) {
    require(trustedEscrowFactories[_msgSender()], "Untrusted Escrow Factory");
    require(trustedErc20Coins[address(escrow.getProductionData().currency)], "Unknown ERC20 token");
    id = tokenIds.current();
    emit ProductionMinted(id, creator, totalAmount, address(escrow));
    productionEscrows[id] = Escrow({id: id, escrow: escrow});
    tokenIds.increment();
    productionToken.mintToken(IProductionTokenTrackerV3(escrow), id, totalAmount);
    productionIdsByOwner[creator].push(id);
  }

  function approve(uint256 id) external override nonReentrant validProduction(id) {
    productionEscrows[id].escrow.approve(_msgSender());
  }

  function decline(uint256 id) external override nonReentrant validProduction(id) {
    productionEscrows[id].escrow.decline(_msgSender());
  }

  function finishCrowdsale(uint256 id) external override nonReentrant validProduction(id) {
    productionEscrows[id].escrow.finish(_msgSender(), msg.sender == relayer, treasury);
  }

  function close(uint256 id) external override nonReentrant validProduction(id) {
    productionEscrows[id].escrow.close(_msgSender(), msg.sender == relayer, treasury);
  }

  function pause(uint256 id) external override nonReentrant validProduction(id) {
    productionEscrows[id].escrow.pause(_msgSender());
  }

  function unpause(uint256 id) external override nonReentrant validProduction(id) {
    productionEscrows[id].escrow.unpause(_msgSender());
  }

  function cancel(uint256 id, uint256 newCloseDate) external override nonReentrant validProduction(id) {
    productionEscrows[id].escrow.cancel(_msgSender(), newCloseDate);
  }

  // ---- Buy Tokens ----

  function buyTokensWithCurrency(
    uint256 id,
    address buyer,
    uint256 amount,
    uint16 perk
  ) external payable override nonReentrant validProduction(id) validBuyer(buyer) {
    require(amount > 0, "Must pass amount > 0");
    require(msg.value > 0, "Must pass msg.value > 0");
    IProductionEscrowV3 escrow = productionEscrows[id].escrow;
    require(amount <= escrow.getTokensAvailable(), "Cannot buy more than available");
    (IERC20Upgradeable token, uint256 price) = escrow.getTokenPrice(amount, buyer);
    swapToTargetTokenAmountOut(token, price, address(escrow));
    emit TokenBought(id, buyer, amount, price, perk);
    escrow.buyTokens(buyer, amount, price, perk);
  }

  function buyTokensWithTokens(
    uint256 id,
    address buyer,
    uint256 amount,
    uint16 perk
  ) external override nonReentrant validProduction(id) validBuyer(buyer) {
    buyWithTransfer(id, amount, _msgSender(), buyer, perk);
  }

  function buyTokensWithFiat(
    uint256 id,
    address buyer,
    uint256 amount,
    uint16 perk
  ) external override nonReentrant validProduction(id) relayerOnly {
    buyWithTransfer(id, amount, msg.sender, buyer, perk);
  }

  // ---- Proceeds and funds ----

  function depositProceedsInTokens(uint256 id, uint256 amount) external override nonReentrant validProduction(id) {
    IProductionEscrowV3.ProductionData memory productionData = productionEscrows[id].escrow.getProductionData();
    IERC20Upgradeable token = IERC20Upgradeable(productionData.currency);
    require(token.allowance(productionData.creator, address(this)) >= amount, "Insufficient allowance");
    token.safeTransferFrom(productionData.creator, address(this), amount);
    token.safeTransfer(address(productionEscrows[id].escrow), amount);
    productionEscrows[id].escrow.depositProceeds(_msgSender(), amount);
    emit ProceedsDeposited(id, _msgSender(), amount);
  }

  function depositProceedsInCurrency(uint256 id) external payable override nonReentrant validProduction(id) {
    IProductionEscrowV3.ProductionData memory productionData = productionEscrows[id].escrow.getProductionData();
    IERC20Upgradeable token = IERC20Upgradeable(productionData.currency);
    uint256 amount = swapToTargetTokenAmountIn(token, address(productionEscrows[id].escrow));
    emit ProceedsDeposited(id, _msgSender(), amount);
    productionEscrows[id].escrow.depositProceeds(_msgSender(), amount);
  }

  function transferProceeds(uint256 id) external override nonReentrant validProduction(id) {
    uint256 amount = productionEscrows[id].escrow.transferProceeds(_msgSender());
    emit ProceedsClaimed(id, _msgSender(), amount);
  }

  function transferFunding(uint256 id) external override nonReentrant validProduction(id) {
    (uint256 amount, uint256 platformShare) = productionEscrows[id].escrow.transferFunding(_msgSender(), treasury);
    emit FundingClaimed(id, _msgSender(), amount, platformShare);
  }

  // ---- Utilities ----

  receive() external payable {}

  function addTrustedEscrowFactory(address trustedEscrowFactory) external onlyOwner {
    trustedEscrowFactories[trustedEscrowFactory] = true;
  }

  function removeTrustedEscrowFactory(address invalidAddress) external onlyOwner {
    trustedEscrowFactories[invalidAddress] = false;
  }

  function isTrustedErc20Token(address candidate) external view override returns (bool) {
    return trustedErc20Coins[candidate];
  }

  function addTrustedErc20Coin(address trustedErc20Coin) external onlyOwner {
    trustedErc20Coins[trustedErc20Coin] = true;
  }

  function removeTrustedErc20Coin(address invalidAddress) external onlyOwner {
    trustedErc20Coins[invalidAddress] = false;
  }

  function setRelayer(address _relayer) external onlyOwner {
    relayer = _relayer;
  }

  // ---- Internal ----

  function _msgSender() internal view override(ContextUpgradeable, ERC2771ContextUpgradeable) returns (address sender) {
    return ERC2771ContextUpgradeable._msgSender();
  }

  function _msgData() internal view override(ContextUpgradeable, ERC2771ContextUpgradeable) returns (bytes calldata) {
    return ERC2771ContextUpgradeable._msgData();
  }

  // ---- Private ----

  function swapToTargetTokenAmountOut(
    IERC20Upgradeable targetToken,
    uint256 targetAmount,
    address targetAddress
  ) private returns (uint256 amountIn) {
    ISwapRouter.ExactOutputSingleParams memory params = ISwapRouter.ExactOutputSingleParams({
      tokenIn: address(nativeWrapper),
      tokenOut: address(targetToken),
      fee: 3000,
      recipient: targetAddress,
      deadline: block.timestamp,
      amountOut: targetAmount,
      amountInMaximum: msg.value,
      sqrtPriceLimitX96: 0
    });
    nativeWrapper.deposit{value: msg.value}();
    TransferHelper.safeApprove(address(nativeWrapper), address(router), msg.value);
    amountIn = router.exactOutputSingle(params);
    if (amountIn < msg.value) {
      // Refund ETH to user
      uint256 refundAmount = msg.value - amountIn;
      TransferHelper.safeApprove(address(nativeWrapper), address(router), 0);
      nativeWrapper.withdraw(refundAmount);
      TransferHelper.safeTransferETH(_msgSender(), refundAmount);
    }
  }

  function swapToTargetTokenAmountIn(IERC20Upgradeable targetToken, address targetAddress)
    private
    returns (uint256 amountIn)
  {
    ISwapRouter.ExactInputSingleParams memory params = ISwapRouter.ExactInputSingleParams({
      tokenIn: address(nativeWrapper),
      tokenOut: address(targetToken),
      fee: 3000,
      recipient: targetAddress,
      deadline: block.timestamp,
      amountIn: msg.value,
      amountOutMinimum: 0,
      sqrtPriceLimitX96: 0
    });
    nativeWrapper.deposit{value: msg.value}();
    TransferHelper.safeApprove(address(nativeWrapper), address(router), msg.value);
    amountIn = router.exactInputSingle(params);
  }

  function buyWithTransfer(
    uint256 id,
    uint256 amount,
    address tokenHolder,
    address buyer,
    uint16 perk
  ) private {
    require(amount > 0, "Must pass amount > 0");
    IProductionEscrowV3 escrow = productionEscrows[id].escrow;
    require(amount <= escrow.getTokensAvailable(), "Cannot buy more than available");
    (IERC20Upgradeable token, uint256 price) = escrow.getTokenPrice(amount, buyer);
    require(token.allowance(tokenHolder, address(this)) >= price, "Insufficient allowance");
    emit TokenBought(id, buyer, amount, price, perk);
    token.safeTransferFrom(tokenHolder, address(this), price);
    token.safeTransfer(address(escrow), price);
    escrow.buyTokens(buyer, amount, price, perk);
  }

  function emptyProduction(uint256 id) private pure returns (Production memory) {
    return
      Production({
        id: id,
        data: IProductionEscrowV3.ProductionData({
          id: id,
          creator: address(0),
          totalSupply: 0,
          organizerTokens: 0,
          soldCounter: 0,
          maxTokensUnknownBuyer: 0,
          currency: IERC20Upgradeable(address(0)),
          state: IProductionEscrowV3.ProductionState.EMPTY,
          dataHash: "",
          crowdsaleEndDate: 0,
          productionEndDate: 0,
          platformSharePercentage: 0,
          perkTracker: IPerkTrackerV3(address(0)),
          priceCalculationEngine: IPriceCalculationEngineV3(address(0))
        }),
        perks: new IProductionEscrowV3.Perk[](0),
        escrow: IProductionEscrowV3(address(0)),
        fundsRaised: 0,
        proceedsEarned: 0,
        escrowBalance: 0,
        paused: false
      });
  }
}

contracts/v3/StaxeProductionTokenV3.sol.content:
//SPDX-License-Identifier: MIT

pragma solidity ^0.8.9;

import "@openzeppelin/contracts-upgradeable/token/ERC1155/ERC1155Upgradeable.sol";
import "@openzeppelin/contracts-upgradeable/access/AccessControlUpgradeable.sol";
import "@openzeppelin/contracts-upgradeable/security/PausableUpgradeable.sol";
import "@openzeppelin/contracts-upgradeable/proxy/utils/Initializable.sol";
import "@openzeppelin/contracts-upgradeable/utils/structs/EnumerableSetUpgradeable.sol";

import "./interfaces/IProductionTokenV3.sol";
import "./interfaces/IProductionTokenTrackerV3.sol";

// import "hardhat/console.sol";

/// @custom:security-contact info@staxe.io
contract StaxeProductionTokenV3 is
  Initializable,
  ERC1155Upgradeable,
  AccessControlUpgradeable,
  PausableUpgradeable,
  IProductionTokenV3
{
  using EnumerableSetUpgradeable for EnumerableSetUpgradeable.UintSet;
  using EnumerableSetUpgradeable for EnumerableSetUpgradeable.AddressSet;

  bytes32 public constant URI_SETTER_ROLE = keccak256("URI_SETTER_ROLE");
  bytes32 public constant PAUSER_ROLE = keccak256("PAUSER_ROLE");
  bytes32 public constant MINTER_ROLE = keccak256("MINTER_ROLE");

  mapping(uint256 => IProductionTokenTrackerV3) private tokenMinter;
  mapping(uint256 => EnumerableSetUpgradeable.AddressSet) private tokenOwnersByToken;
  mapping(address => EnumerableSetUpgradeable.UintSet) private tokenIdsByOwner;

  /// @custom:oz-upgrades-unsafe-allow constructor
  constructor() {
    _disableInitializers();
  }

  function initialize() public initializer {
    __ERC1155_init("https://staxe.io/api/v3/tokens/{id}");
    __AccessControl_init();
    __Pausable_init();

    _grantRole(DEFAULT_ADMIN_ROLE, msg.sender);
    _grantRole(URI_SETTER_ROLE, msg.sender);
    _grantRole(PAUSER_ROLE, msg.sender);
    _grantRole(MINTER_ROLE, msg.sender);
  }

  function getTokenBalances(address buyer)
    external
    view
    returns (uint256[] memory tokenIds, uint256[] memory balances)
  {
    require(buyer != address(0), "Invalid buyer");
    tokenIds = EnumerableSetUpgradeable.values(tokenIdsByOwner[buyer]);
    balances = new uint256[](tokenIds.length);
    for (uint256 i = 0; i < tokenIds.length; i++) {
      balances[i] = balanceOf(buyer, tokenIds[i]);
    }
  }

  function getTokenOwners(uint256 id) external view returns (address[] memory owners, uint256[] memory balances) {
    require(id > 0, "Invalid tokenId");
    owners = EnumerableSetUpgradeable.values(tokenOwnersByToken[id]);
    balances = new uint256[](owners.length);
    for (uint256 i = 0; i < owners.length; i++) {
      balances[i] = balanceOf(owners[i], id);
    }
  }

  function setURI(string memory newuri) public onlyRole(URI_SETTER_ROLE) {
    _setURI(newuri);
  }

  function pause() public onlyRole(PAUSER_ROLE) {
    _pause();
  }

  function unpause() public onlyRole(PAUSER_ROLE) {
    _unpause();
  }

  function mint(
    address account,
    uint256 id,
    uint256 amount,
    bytes memory data
  ) public onlyRole(MINTER_ROLE) {
    _mint(account, id, amount, data);
  }

  function mintBatch(
    address to,
    uint256[] memory ids,
    uint256[] memory amounts,
    bytes memory data
  ) public onlyRole(MINTER_ROLE) {
    _mintBatch(to, ids, amounts, data);
  }

  function mintToken(
    IProductionTokenTrackerV3 owner,
    uint256 id,
    uint256 totalAmount
  ) external override {
    require(totalAmount > 0, "Must pass amount > 0");
    tokenMinter[id] = owner;
    mint(address(owner), id, totalAmount, "");
  }

  function _beforeTokenTransfer(
    address, /*operator*/
    address from,
    address to,
    uint256[] memory ids,
    uint256[] memory amounts,
    bytes memory /*data*/
  ) internal override {
    for (uint256 i = 0; i < ids.length; i++) {
      uint256 amount = amounts[i];
      uint256 id = ids[i];
      EnumerableSetUpgradeable.add(tokenOwnersByToken[id], to);
      EnumerableSetUpgradeable.add(tokenIdsByOwner[to], id);
      IProductionTokenTrackerV3 tracker = tokenMinter[id];
      if (shouldCallTokenTransferTracker(from, to, address(tracker))) {
        tracker.onTokenTransfer(this, ids[i], from, to, amount);
      }
    }
  }

  function shouldCallTokenTransferTracker(
    address from,
    address to,
    address tracker
  ) private view returns (bool) {
    return tracker != address(0) && from != address(this) && from != tracker && to != tracker;
  }

  function supportsInterface(bytes4 interfaceId)
    public
    view
    override(ERC1155Upgradeable, IERC165Upgradeable, AccessControlUpgradeable)
    returns (bool)
  {
    return super.supportsInterface(interfaceId);
  }
}

contracts/v3/StaxePurchaseProxyV3.sol.content:
//SPDX-License-Identifier: MIT

pragma solidity ^0.8.9;

import "@openzeppelin/contracts-upgradeable/metatx/ERC2771ContextUpgradeable.sol";
import "@openzeppelin/contracts-upgradeable/token/ERC20/ERC20Upgradeable.sol";
import "@openzeppelin/contracts-upgradeable/token/ERC20/IERC20Upgradeable.sol";
import "@openzeppelin/contracts-upgradeable/access/OwnableUpgradeable.sol";
import "@openzeppelin/contracts-upgradeable/security/PausableUpgradeable.sol";
import "@openzeppelin/contracts-upgradeable/security/ReentrancyGuardUpgradeable.sol";
import "@openzeppelin/contracts-upgradeable/token/ERC20/extensions/IERC20MetadataUpgradeable.sol";
import "@openzeppelin/contracts-upgradeable/token/ERC20/utils/SafeERC20Upgradeable.sol";

import "./interfaces/IProductionsV3.sol";
import "./interfaces/IProductionEscrowV3.sol";
import "./interfaces/IPurchaseProxyV3.sol";

// import "hardhat/console.sol";

/// @custom:security-contact info@staxe.io
contract StaxePurchaseProxyV3 is
  IPurchaseProxyV3,
  ERC20Upgradeable,
  ERC2771ContextUpgradeable,
  OwnableUpgradeable,
  PausableUpgradeable,
  ReentrancyGuardUpgradeable
{
  using SafeERC20Upgradeable for IERC20Upgradeable;

  IProductionsV3 private productions;
  IERC20Upgradeable private wrappedToken;
  mapping(address => Purchase) private purchases;

  /// @custom:oz-upgrades-unsafe-allow constructor
  constructor(address _trustedForwarder) ERC2771ContextUpgradeable(_trustedForwarder) {
    _disableInitializers();
  }

  function initialize(IProductionsV3 _productions, IERC20Upgradeable _wrappedToken) public initializer {
    __Ownable_init();
    __ERC20_init("Staxe Wrapper Tokens", "WSTX");
    __Pausable_init();
    __ReentrancyGuard_init();
    productions = _productions;
    wrappedToken = _wrappedToken;
  }

  // ---- Functions ----

  function decimals() public view override returns (uint8) {
    return IERC20MetadataUpgradeable(address(wrappedToken)).decimals();
  }

  function placePurchase(
    uint256 tokenId,
    uint256 numTokens,
    uint16 perkId
  ) external override nonReentrant whenNotPaused {
    require(
      productions.getProduction(tokenId).data.state == IProductionEscrowV3.ProductionState.OPEN,
      "Proudction not open for token sales"
    );
    require(numTokens != 0, "Invalid token number to buy");
    emit PurchasePlaced(_msgSender(), tokenId, numTokens, perkId);
    purchases[_msgSender()] = Purchase({tokenId: tokenId, numTokens: numTokens, perkId: perkId});
  }

  function purchase(
    uint256 tokenId,
    uint256 numTokens,
    uint16 perkId
  ) external override nonReentrant whenNotPaused {
    require(
      productions.getProduction(tokenId).data.state == IProductionEscrowV3.ProductionState.OPEN,
      "Proudction not open for token sales"
    );
    require(numTokens != 0, "Invalid token number to buy");
    _purchase(_msgSender(), tokenId, numTokens, perkId, true);
  }

  function depositTo(
    address buyer,
    uint256 tokenAmount,
    address tokenAddress
  ) external override nonReentrant whenNotPaused {
    require(tokenAddress == address(wrappedToken), "Invalid token sent");
    IERC20Upgradeable token = IERC20Upgradeable(tokenAddress);
    token.safeTransferFrom(_msgSender(), address(this), tokenAmount);
    _mint(buyer, tokenAmount);
    if (purchases[buyer].tokenId != 0) {
      bool result = _purchase(
        buyer,
        purchases[buyer].tokenId,
        purchases[buyer].numTokens,
        purchases[buyer].perkId,
        false
      );
      if (result) {
        purchases[buyer].tokenId = 0;
      }
    }
  }

  function withdraw(uint256 amount) external override nonReentrant whenNotPaused {
    require(amount > 0, "Amount must be > 0");
    require(balanceOf(_msgSender()) >= amount, "Balance not high enough");
    _burn(_msgSender(), amount);
    wrappedToken.safeTransfer(_msgSender(), amount);
  }

  function withdrawAll() external override nonReentrant whenNotPaused {
    uint256 balance = balanceOf(_msgSender());
    require(balance > 0, "Nothing to withdraw");
    _burn(_msgSender(), balance);
    wrappedToken.safeTransfer(_msgSender(), balance);
  }

  // ---- Admin ----

  function pause() external onlyOwner {
    _pause();
  }

  function unpause() external onlyOwner {
    _unpause();
  }

  // ---- Internal ----

  function _purchase(
    address buyer,
    uint256 tokenId,
    uint256 numTokens,
    uint16 perkId,
    bool requireSufficientBalance
  ) internal returns (bool) {
    (IERC20Upgradeable productionCurrency, uint256 price) = productions.getTokenPriceFor(tokenId, numTokens, buyer);
    require(address(productionCurrency) == address(wrappedToken), "Invalid payment currency");
    require(!requireSufficientBalance || balanceOf(buyer) >= price, "Balance too low");
    if (balanceOf(buyer) >= price) {
      emit PurchaseExecuted(buyer, tokenId, numTokens, perkId, address(productionCurrency), price);
      _burn(buyer, price);
      wrappedToken.safeApprove(address(productions), price);
      productions.buyTokensWithTokens(tokenId, buyer, numTokens, perkId);
      return true;
    }
    return false;
  }

  function _msgSender() internal view override(ContextUpgradeable, ERC2771ContextUpgradeable) returns (address sender) {
    return ERC2771ContextUpgradeable._msgSender();
  }

  function _msgData() internal view override(ContextUpgradeable, ERC2771ContextUpgradeable) returns (bytes calldata) {
    return ERC2771ContextUpgradeable._msgData();
  }
}
